function Player:initRecharge()
    local config = Player.getMinGCubeConfig(1)
    local rechargeData = self:data("RechargeData")
    if config ~= nil then
        rechargeData.enableSumRecharge = 1
    else
        rechargeData.enableSumRecharge = 0
    end
    rechargeData.enableTimeRecharge = 0
end

function Player:receiveSumRecharge()
    local packet = { pid = "SumRechargeReceive" }
    self:sendPacket(packet)
end