function UISubAnimationTextScaleXYZ:onPlay()
    self.scaleStart = Lib.strToV3(self.config.scaleStart)
    self.scaleEnd = Lib.strToV3(self.config.scaleEnd)
    self.scale = self.scaleStart
    self.scaleDelta = (self.scaleEnd - self.scaleStart) / self.ticks
