local Def = require "we.def"

function ItemDataUtils:gameRootPath()
    if DataLink:useDataLink() then
        return Def.PATH_GAME_LINK_DIR
    else
        return Def.PATH_GAME_ROOT_DIR
    end
end


function ItemDataUtils:delDir(path)
    if DataLink:useDataLink() then
         DataLink:delDir(path)
    else
        os.execute(string.format([[RD /S/Q "%s"]], path))
    end
end

function ItemDataUtils:delFile(path)
    if DataLink:useDataLink() then
        DataLink:delFile(path)
    else
        os.remove(path)
    end
end

function ItemDataUtils:del(path)
    if not DataLink:useDataLink() then
        VFS:del(path)
    end
end

return ItemDataUtils
